import axios from "axios"

export const loginCall = async (userCreds, dispatch) => {
    dispatch({type: "LOGIN_START"});
    try{
        const res = await axios.post("auth/login", userCreds);
        dispatch({type: "LOGIN_SUCCESS", payload: res.data });
    }catch(err){
        dispatch({type: "LOGIN_FAILURE", payload: err });
    }
};

export const logoutCall = async (dispatch) => {
    localStorage.removeItem("user");
    dispatch({ type: "LOGOUT" });
    
};

export const updateUserInfo = async (user, newUserInfo, updateContextUser) => {
    try {
        axios.put(`/users/${user._id}`, newUserInfo);      
        updateContextUser(newUserInfo); 
    } catch (err) {
      console.log(err);
    }
  };
