import React, { useContext, useState, useRef } from "react";
import { AuthContext} from "../../context/AuthContext";
import Dialog from '@mui/material/Dialog';
import DialogContent from '@mui/material/DialogContent';
import EditIcon from '@mui/icons-material/Edit';
import "./profileEditDialog.css";
import axios from "axios";



const ProfileEditDialog = () => {
  const updatedUsernameRef = useRef(user.username);
  const updatedBioRef = useRef('');
  const updatedPronounsRef = useRef('');
  const updatedBirthdayRef = useRef('');
  const updatedCityRef = useRef('');
  const {user} = useContext(AuthContext);
  
  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleSaveChanges = async () => {
    const updatedUsername = updatedUsernameRef.current;
    const updatedBio = updatedBioRef.current;
    const updatedPronouns = updatedPronounsRef.current;
    const updatedBirthday = updatedBirthdayRef.current;
    const updatedCity = updatedCityRef.current;

    if(file){
      const fileName = Date.now() + file.name;
      formData.append("name", fileName);
      formData.append("file",file);
    }
    if(coverFile){
      const coverFileName = Date.now() + file.name;
      formData.append("name", coverFileName);
      formData.append("file", coverFile);
    }

    try {
      const updatedUserData = {
        username: updatedUsername,
        bio: updatedBio,
        pronouns: updatedPronouns,
        birthday: updatedBirthday,
        city: updatedCity,
        userId: user._id // Assuming user._id exists
      };
      await axios.put(`/users/${user._id}`, updatedUserData);
      setOpen(false);
    }catch(err){
      console.log(err);
    }

   
  };

  const handleChange = (event) => {
    setBio(event.target.value);
    event.target.style.height = 'auto';
    event.target.style.height = `${event.target.scrollHeight}px`;
  };

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleCoverPictureChange = (event) => {
    setCoverFile(event.target.files[0]);
  };

  return (
    <div>
      <button className="editButton" onClick={handleOpen}>
        Edit Profile&nbsp;<EditIcon />
      </button>
      <Dialog open={open} onClose={handleClose}>
        <div className="dialogTitle">Edit Profile</div>
        <hr className="dialogHr" />
        <DialogContent>
          <form className='editProfileForm'>
            <div className="editProfileInputItem">
              <label 
                className="editProfileLabel"
              >
                Username:
              </label>
              <input 
                className="editProfileInput" 
                type="text" 
                defaultValue={user.username}
                ref={updatedUsernameRef}
                maxLength={20}
              />
            </div>
            <div className="editProfileInputItem">
              <label className="editProfileLabel" >Bio:</label>
              <textarea 
                className="editProfileInput" 
                style={{ resize: 'none' }}
                rows="1" 
                maxLength="50"
                value={user.desc} 
                onChange={handleChange}
              />
            </div>
            <div className="editProfileInputItem">
              <label className="editProfileLabel">Pronouns: </label>
              <select className="editProfileInput" defaultValue={user.pronouns}>
                <option value="0">-</option>
                <option value="1">She/her</option>
                <option value="2">She/they</option>
                <option value="3">He/they</option>
                <option value="4">He/him</option>
                <option value="5">They/them</option>
                <option value="6">None</option>
              </select>
            </div>
            <div className="editProfileInputItem">
              <label className="editProfileLabel">Birthday: </label>
              <input className="editProfileInput" type="text" maxLength={20} placeholder={user.birthday} />
            </div>
            <div className="editProfileInputItem">
              <label className="editProfileLabel">City: </label>
              <input className="editProfileInput" type="text" maxLength={30} placeholder={user.city} />
            </div>
            <div className="editProfileInputItem">
              <label className="editProfileLabel">Profile Picture:</label>
              <input
                type="file"
                accept=".jpg, .jpeg, .png"
                onChange={handleFileChange}
              />
            
            </div>
            <div className="editProfileInputItem">
              <label className="editProfileLabel">Cover Picture:</label>
              <input
                type="file"
                accept=".jpg, .jpeg, .png"
                onChange={handleCoverPictureChange}
              />
            </div>
          </form>
        </DialogContent>
        <span className='editProfileActions'>
          <button className="editProfileCancel" onClick={handleClose}>Cancel</button>
          <button className="editProfileSave"onClick={handleSaveChanges}>
            Save
          </button>
        </span>
      </Dialog>
    </div>
  );
};

export default ProfileEditDialog;
