import axios from "axios"

export const loginCall = async (userCreds, dispatch) => {
    dispatch({type: "LOGIN_START"});
    try{
        const res = await axios.post("auth/login", userCreds);
        dispatch({type: "LOGIN_SUCCESS", payload: res.data });
    }catch(err){
        dispatch({type: "LOGIN_FAILURE", payload: err });
    }
};

export const logoutCall = async (dispatch) => {
    localStorage.removeItem("user");
    dispatch({ type: "LOGOUT" });
    
};

export const updateUserInfo = async (user, updatedUserData, dispatch) => {
    try {
      // Assuming you have an API endpoint for updating user information
      const response = await axios.put(`/users/${user._id}`, updatedUserData);
      
      // Assuming the response data contains the updated user information from the backend
      const updatedUser = response.data;
      
      // Dispatch an action to update the user in the context or local storage
      dispatch({ type: "UPDATE_USER", payload: updatedUser });
    } catch (error) {
      console.error("Error updating user information:", error);
      throw error;
    }
  };
