import axios from "axios"

export const loginCall = async (userCreds, dispatch) => {
    dispatch({type: "LOGIN_START"});
    try{
        const res = await axios.post("auth/login", userCreds);
        dispatch({type: "LOGIN_SUCCESS", payload: res.data });
    }catch(err){
        dispatch({type: "LOGIN_FAILURE", payload: err });
    }
};

export const logoutCall = async (dispatch) => {
    localStorage.removeItem("user");
    dispatch({ type: "LOGOUT" });
    
};

export const updateUserInfo= async (userInfo, dispatch) => {
    try {
        const updatedUserData = {
          desc: updatedBio,
          pronouns: updatedPronouns,
          birthday: updatedBirthday,
          city: updatedCity,
          profilePicture: pfpName? pfpName: user.profilePicture,
          coverPicture: coverFileName? coverFileName: user.coverPicture,
        };
        await axios.put(`/users/${user._id}`, updatedUserData);
        setOpen(false);
        window.location.reload();
        dispatch({ type: "USER_UPDATE" });
      }catch(err){
        console.log(err);
      }
};
